<div id="avail-form" class="full-height availability-form"><div class="request-form"><form role="form" action="meetings" method="post"><div class="form-group"><label for="email">Email address</label><input type="email" class="form-control" name="email" id="email" placeholder="Enter email"></div><div class="form-group"><label for="">Name</label><input type="text" class="form-control" name="name" id="name" placeholder="Enter name"></div><div class="form-group"><label for="">Notes</label><textarea rows="10" class="form-control" name="notes" id="notes" placeholder="Write a message"></textarea></div><input  type="hidden" name="availability_id" value="{{$availability->id}}"><input  type="hidden" name="advisor_id"      value="{{$availability->advisors()->first()->id}}"><input  type="hidden" name="location_id"     value="{{$availability->locations()->first()->id}}"><input  type="hidden" name="service_id"      value="{{$availability->services()->first()->id}}"><button type="submit" class="btn btn-default">Submit</button></form></div></div>